<?php namespace Tests\Media;

use Storage;
use Nova\Foundation\Media;
use Tests\DatabaseTestCase;

class ManageMediaTest extends DatabaseTestCase
{
	/** @test **/
	public function unauthenticated_users_cannot_manage_media()
	{
		$this->withExceptionHandling();

		$media = create(Media::class);

		$this->post(route('media.store'))->assertRedirect(route('login'));
		$this->patch(route('media.update', $media))->assertRedirect(route('login'));
		$this->delete(route('media.destroy', $media))->assertRedirect(route('login'));
		$this->patch(route('media.reorder'))->assertRedirect(route('login'));
	}

	/** @test **/
	public function media_can_be_created()
	{
		$this->signIn();

		$character = create('Nova\Characters\Character');

		$data = [
			'location' => 'characters',
			'image' => $this->base64Image(),
			'id' => $character->id,
			'type' => 'character',
		];

		$this->post(route('media.store'), $data);

		$media = Media::first();

		$this->assertDatabaseHas('media', [
			'id' => 1,
			'mediable_type' => 'character',
			'mediable_id' => $character->id,
			'filename' => $media->filename,
			'primary' => (int) true
		]);

		$this->assertFileExists(storage_path("app/public/characters/{$media->filename}"));

		deletor(Media::class)->delete($media);
	}

	/** @test **/
	public function media_can_be_reordered()
	{
		$this->signIn();

		$media1 = create(Media::class);
		$media2 = create(Media::class);
		$media3 = create(Media::class);

		$response = $this->patch(route('media.reorder'), ['media' => [$media2->id, $media3->id, $media1->id]]);

		$response->assertStatus(200);

		$this->assertDatabaseHas('media', ['id' => $media2->id, 'order' => 0]);
		$this->assertDatabaseHas('media', ['id' => $media3->id, 'order' => 1]);
		$this->assertDatabaseHas('media', ['id' => $media1->id, 'order' => 2]);
	}

	/** @test **/
	public function media_can_be_deleted()
	{
		$this->signIn();

		$media = create(Media::class);

		$response = $this->delete(route('media.destroy', $media));

		$response->assertStatus(200);

		$this->assertDatabaseMissing('media', ['id' => $media->id]);
	}

	/** @test **/
	public function media_can_be_set_as_the_primary_object()
	{
		$this->signIn();

		$character = create('Nova\Characters\Character');

		$media1 = create(Media::class, [
			'mediable_type' => 'character',
			'mediable_id' => $character->id,
			'primary' => (int) true
		]);

		$media2 = create(Media::class, [
			'mediable_type' => 'character',
			'mediable_id' => $character->id,
			'primary' => (int) false
		]);

		$response = $this->patch(route('media.update', $media2));

		$response->assertStatus(200);

		$this->assertDatabaseHas('media', ['id' => $media1->id, 'primary' => 0]);
		$this->assertDatabaseHas('media', ['id' => $media2->id, 'primary' => 1]);
	}

	public function the_first_media_item_is_set_as_the_primary_media()
	{
		$this->signIn();

		$character = create('Nova\Characters\Character');

		$media = create(Media::class, [
			'mediable_type' => 'character',
			'mediable_id' => $character->id,
		]);
	}

	protected function base64Image()
	{
		return "data:image/jpeg;base64,";
	}
}
